SELECT row_to_json(f) AS feature FROM (
   SELECT
      'Feature' AS type,
      ST_AsGeoJSON(ST_Transform(way,4326))::json AS geometry,
      row_to_json((SELECT l FROM (SELECT gid AS id, CASE WHEN tags->'name:en' <> '' THEN tags->'name:en' ELSE name END AS name) AS l)) AS properties
   FROM planet_osm_line
   WHERE
	highway IN (
		'motorway',
		'trunk',
		'primary',
		'secondary',
		'tertiary',
		'unclassified',
		'residential',
		'service',
		'motorway_link',
		'trunk_link',
		'primary_link',
		'secondary_link',
		'tertiary_link',
		'living_street',
		'road',
		'turning_circle') AND
	ST_Intersects(way, ST_GeomFromText('POLYGON ((
		12718130.2139427568763494 2572793.9350507482886314,
		12726691.1611106973141432 2582119.2525015394203365,
		12740526.2632303126156330 2585864.6668875128962100,
		12757074.8798540551215410 2597368.4396444312296808,
		12774234.9927040766924620 2609483.7086276300251484,
		12699173.8309280332177877 2646326.3562610866501927,
		12638482.8304696045815945 2648007.9708833592012525,
		12644903.5408455580472946 2605967.6053265123628080,
		12660649.5686723049730062 2598629.6506111351773143,
		12667223.1531048305332661 2583495.1190106710419059,
		12676395.5964990500360727 2567290.4690142129547894,
		12685491.6028649862855673 2563315.7435433859936893,
		12694587.6092309225350618 2564232.9878828078508377,
		12718130.2139427568763494 2572793.9350507482886314))', 3857))
) AS f;